package mypack;

public class DoWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//int i =20;  it prints 20 and condition failed
		int i =1;  // it prints 1 and condition is passed .so print 2 to 10
		do
		{
			System.out.println(i);
			i++;
		} while(i<=10);

	}

}
