package mypack;

public class Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int t =10;
		int s=20;
		//Arthemetic Operators
		System.out.println(t-s);
		System.out.println(t+s);
		System.out.println(t/s);
		System.out.println(t*s);
		System.out.println(t%s);
		
		// Conditional Operators
		System.out.println(t==s);
		System.out.println(t!=s);
		System.out.println(t>=s);
		System.out.println(t<=s);
		
		//Logical Operators
		
		//incremental Operators
		int a =10;
		//a =a+1;
		a++;
		a--;
		System.out.println(a);
		
		
		
				

	}

}
